import os
import logging

logging.basicConfig(level=os.environ.get("IOT_LOGLEVEL", "ERROR"))
_log = logging.getLogger("simpleiot")

LOG_WITH_LOGGER = False

def ldebug(msg):
    if LOG_WITH_LOGGER:
        _log.debug(msg)
    else:
        print(msg)

def linfo(msg):
    if LOG_WITH_LOGGER:
        _log.info(msg)
    else:
        print(msg)

def lwarning(msg):
    if LOG_WITH_LOGGER:
        _log.warning(msg)
    else:
        print(msg)

def lerror(msg):
    if LOG_WITH_LOGGER:
        _log.error(msg)
    else:
        print(msg)

